mapper.select:
SELECT faq_no faqNo, account_id accountId, faq_writer faqWriter, faq_title faqTitle, faq_content faqContent, faq_createdate faqCreatedate, faq_count faqCount, faq_category faqCategory FROM faq
AND faq_category = #{searchCategory}
AND faq_title LIKE CONCAT('%' , #{searchText} , '%')
AND faq_content LIKE CONCAT('%' , #{searchText} , '%')
AND (faq_title LIKE CONCAT('%' , #{searchText} , '%') OR faq_content LIKE CONCAT('%' , #{searchText} , '%'))
ORDER BY faq_createdate DESC LIMIT #{beginRow}, #{rowPerPage}
