mapper.select:
SELECT note_no noteNo, note_title noteTitle, note_content noteContent, note_dispatcher_id noteDispatcherId, note_dispatcher_name noteDispatcherName, note_receiver_id noteReceiverId, note_receiver_name noteReceiverName, note_send_date noteSendDate, note_is_read noteIsRead FROM note WHERE note_receiver_id = #{accountId} AND note_delete != 'receiver'
AND (note_title LIKE CONCAT('%' , #{searchText} , '%') OR note_content LIKE CONCAT('%' , #{searchText} , '%'))
ORDER BY note_send_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT note_no noteNo, note_title noteTitle, note_content noteContent, note_dispatcher_id noteDispatcherId, note_dispatcher_name noteDispatcherName, note_receiver_id noteReceiverId, note_receiver_name noteReceiverName, note_send_date noteSendDate, note_is_read noteIsRead FROM note WHERE note_dispatcher_id = #{accountId} AND note_delete != 'dispatcher'
AND (note_title LIKE CONCAT('%' , #{searchText} , '%') OR note_content LIKE CONCAT('%' , #{searchText} , '%'))
ORDER BY note_send_date DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.update:
UPDATE
	note
SET
	note_delete = #{noteDelete}
WHERE
	note_no = #{noteNo}
